/**
 * Join a string with zero-width space.
 * @param inputs
 * @returns
 */
declare function joinToString(inputs: string[]): string;
/**
 * Remove duplicate zero-width space from string
 * @param input
 * @returns
 */
declare function normalize(input: string): string;
/**
 * Remove all zero-width space from a string.
 * @param input input string
 * @returns A string without zero-width space character.
 */
declare function sanitize(input: string): string;
declare function tokenize(input: string): {
    start: number;
    end: number;
    value: string;
}[];
declare function split(input: string): string[];

export { joinToString, normalize, sanitize, split, tokenize };
